import React, { useState, useContext } from "react";
import { NavLink } from "react-router-dom";
import Data from "../data";
import styled from "styled-components";
import { AgentContext } from "../Context/AgentsContext";

const AgentCardContainer = styled.div`
  display: flex;
  width: 50%;
  min-height: 300px;
  margin: 25px auto;
  /* border: 2px solid black; */
  border-radius: 15px;
  background-color: white;
  box-shadow: 8px 8px 4px #ccac08, -8px -8px 4px #eec006;

  @media only screen and (max-width: 1078px) {
    flex-wrap: wrap;
    .userCard__Container {
      width: 100%;
      height: 50%;
      order: 2;
    }
    .userCard__contactInfo {
      margin: auto;
      width: 50%;
      order: 3;
    }
    .userCard__xBtn {
      order: 1;
    }
  }
  .userCard__Container {
    margin: 15px;
    margin-top: 35px;
    .userCard__Img {
      height: 220px;
      width: 200px;
      border-radius: 10px;
      object-fit: cover;
    }
  }
  .userCard__contactInfo {
    text-align: left;
    margin: auto;
    p {
      padding: 5px;
    }
  }
  .userCard__xBtn {
    margin-top: 5px;
    margin-right: 5px;
  }
`;

function AgentCard(props) {
  const [agentsList, setAgentsList] = useContext(AgentContext);
  const agent = agentsList[0];
  return (
    <AgentCardContainer key={agentsList[0]}>
      <div className="userCard__Container">
        <img
          src={`https://www.wellandgood.com/wp-content/uploads/2019/07/GettyImages-1159767394.jpg`}
          alt="agent profile"
          height="200"
          className="userCard__Img"
        />
      </div>
      <div className="userCard__contactInfo">
        <p>Name: {data.name}</p>
        <p>Title: {data.title}</p>
        <p>Phone: {data.phone}</p>
        <p>Email: {data.email}</p>
        <p>Reports to: {data.reports} </p>
      </div>
      <div className="userCard__xBtn">
        <NavLink to="/support-team">X</NavLink>
      </div>
    </AgentCardContainer>
  );
}

export default AgentCard;
